    constructor(options: IPepTextboxFieldOptions = {}) {
        super(options);

        this.type = options.type || 'text';

        this.update(options);
    }

    public update(options: IPepTextboxFieldOptions): void {
        super.update(options);

        if (this.type === 'link') {
