    // zoomOut(): void {
    //     if (this.ngxViewerImage.scale > this.ngxViewerImage.config.zoomFactor) {
    //         this.ngxViewerImage.scale /= (1 + this.ngxViewerImage.config.zoomFactor);
    //     }
    //     this.updateStyle();
