export interface IPepRichHtmlTextareaToolbarOptions {
    font?: any;
    size?: any;
    header?: any;
    bold?: any;
    italic?: any;
    underline?: any;
    strike?: any;
    link?: any;
    image?: any;
    ordered?: any;
