import { Injectable } from '@angular/core';
import { PepSessionService } from './session.service';
import { PepHttpService } from '../../http/services/http.service';
import { Observable } from 'rxjs';

/*
    This service is the webapp api for addon usege.
*/
@Injectable({
    providedIn: 'root',
})
export class PepAddonService {
    private readonly ADDON_ASSETS_PATH_KEY = 'AddonAssetsPath';
    private readonly ADDON_API_RELATIVE_PATH = '/addons/api';
    private readonly ADDON_API_ASYNC_RELATIVE_PATH = `${this.ADDON_API_RELATIVE_PATH}/async`;

    constructor(
        private sessionService: PepSessionService,
        private httpService: PepHttpService
    ) {}

    private getAddonBaseRelativePath(isAsync: boolean): string {
        return isAsync
            ? this.ADDON_API_ASYNC_RELATIVE_PATH
            : this.ADDON_API_RELATIVE_PATH;
    }

    getAddonStaticFolder(): string {
        // return this.assetsPath;
        return this.sessionService.getObject(this.ADDON_ASSETS_PATH_KEY) || '';
    }

    setAddonStaticFolder(path: string): void {
        // this.assetsPath = path;
        return this.sessionService.setObject(this.ADDON_ASSETS_PATH_KEY, path);
    }

    getAddonApiCall(
        addonUUID: string,
        fileName: string,
        functionName: string,
        httpOptions = {},
        isAsync = false
    ): Observable<any> {
        return this.httpService.getPapiApiCall(
            `${this.getAddonBaseRelativePath(
                isAsync
            )}/${addonUUID}/${fileName}/${functionName}`,
            httpOptions
        );
    }

    postAddonApiCall(
        addonUUID: string,
        fileName: string,
        functionName: string,
        body = {},
        httpOptions = {},
        isAsync = false
    ): Observable<any> {
        return this.httpService.postPapiApiCall(
            `${this.getAddonBaseRelativePath(
                isAsync
            )}/${addonUUID}/${fileName}/${functionName}`,
            body,
            httpOptions
        );
    }
}

import {
    Component,
    Input,
    OnInit,
    Injectable,
    Output,
    EventEmitter,
} from '@angular/core';
import { PepLayoutService, PepScreenSizeType } from '@pepperi-addons/ngx-lib';
import {
    IPepBreadCrumbItemClickEvent,
    PepBreadCrumbItem,
    PepBreadCrumbsDisplayType,
} from './bread-crumbs.model';
import {
    pepIconArrowLeft,
    pepIconArrowRight,
} from '@pepperi-addons/ngx-lib/icon';
@Component({
    selector: 'pep-bread-crumbs',
    templateUrl: './bread-crumbs.component.html',
    styleUrls: ['./bread-crumbs.component.scss'],
})
@Injectable()
export class PepBreadCrumbsComponent implements OnInit {
    private _items: Array<PepBreadCrumbItem> = [];
    @Input()
    set items(value: Array<PepBreadCrumbItem>) {
        this._items = value;
        this.setupShrinkItems();
    }
    get items(): Array<PepBreadCrumbItem> {
        return this._items;
    }

    private _displayType: PepBreadCrumbsDisplayType = 'label';
    @Input()
    set displayType(value: PepBreadCrumbsDisplayType) {
        this._displayType = value;
        this.setupShrinkItems();
    }
    get displayType(): PepBreadCrumbsDisplayType {
        return this._displayType;
    }

    @Input() addSpacing = false;

    @Output()
    itemClick: EventEmitter<IPepBreadCrumbItemClickEvent> = new EventEmitter<IPepBreadCrumbItemClickEvent>();

    charSeparator = ' / ';
    iconSeparator: string = pepIconArrowRight.name;
    shrinkItems = false;
    screenSize: PepScreenSizeType;

    constructor(private layoutService: PepLayoutService) {}

    ngOnInit(): void {
        this.layoutService.onResize$.pipe().subscribe((size) => {
            this.screenSize = size;
            this.setupShrinkItems();
        });

        if (this.layoutService.isRtl()) {
            this.charSeparator = ' \\ ';
            this.iconSeparator = pepIconArrowLeft.name;
        }
    }

    private setupShrinkItems() {
        if (this.displayType === 'items' && this.items.length > 1) {
            this.shrinkItems = this.screenSize > PepScreenSizeType.SM;
        }
    }

    getLabelTitle(): string {
        let labelTitle = '';

        if (this.items && this.items.length > 0) {
            for (let index = 0; index < this.items.length; index++) {
                if (index < this.items.length - 1) {
                    if (index > 0) {
                        labelTitle += this.charSeparator;
                    }

                    labelTitle += this.items[index].text;
                }
            }
        }

        return labelTitle;
    }

    onBreadCrumbItemClick(item: PepBreadCrumbItem): void {
        this.itemClick.emit({ source: item });
    }
}

