        this.getDefaultThemeFormCustomizationVariables(themeVars);

        // Declare default card customization.
        this.getDefaultThemeCardCustomizationVariables(themeVars);

        // Declare default table customization.
        this.getDefaultThemeTableCustomizationVariables(themeVars);

        // Declare default qs customization.
        this.getDefaultThemeQSCustomizationVariables(themeVars);

        // Declare default top header customization.
        this.getDefaultThemeTopHeaderCustomizationVariables(themeVars);

        return themeVars;
    }

    getThemeVariables(): any {
        const ret = this.getDefaultThemeVariables();
        const themeVars = this.sessionService.getObject(
            PepCustomizationService.USER_THEME_VARIABLES
        );

        // Override defaults.
        if (themeVars) {
            // tslint:disable-next-line: forin
            for (const key in themeVars) {
                ret[key] = themeVars[key];
            }
        }

        return ret;
    }

    setThemeVariables(themeVariablesToSet = null): void {
        const themeVariables = this.getThemeVariables();

        // Override defaults.
        if (themeVariablesToSet) {
            // tslint:disable-next-line: forin
            for (const key in themeVariablesToSet) {
                themeVariables[key] = themeVariablesToSet[key];
            }
        }

        this.sessionService.setObject(
            PepCustomizationService.USER_THEME_VARIABLES,
            JSON.stringify(themeVariables)
        );

        // tslint:disable-next-line: forin
        for (const key in themeVariables) {
            if (this.isFontUrlKey(key)) {
                this.fileService.loadFontStyle(key, themeVariables[key]);
            } else {
                document.documentElement.style.setProperty(
                    key,
                    themeVariables[key]
                );
            }
        }
    }

    isFontUrlKey(key: string): boolean {
        let res = false;

        if (
            key === PepCustomizationService.FONT_FAMILY_TITLE_NORMAL_URL_KEY ||
            key === PepCustomizationService.FONT_FAMILY_TITLE_BOLD_URL_KEY ||
            key === PepCustomizationService.FONT_FAMILY_BODY_NORMAL_URL_KEY ||
            key === PepCustomizationService.FONT_FAMILY_BODY_BOLD_URL_KEY
        ) {
            res = true;
        }

        return res;
    }

    getThemeVariable(key: string): string {
        const themeVars = this.getThemeVariables();

        if (themeVars.hasOwnProperty(key)) {
            return themeVars[key];
        } else {
            return '';
        }
    }

    getNumberThemeVariable(themeVars, key: string): number {
        if (themeVars && themeVars.hasOwnProperty(key)) {
            // If it's reference to another key.
            if (themeVars[key].indexOf('var(') === 0) {
                key = themeVars[key].substr(4).split(')')[0];
            }

            const tmp = Number(
                themeVars[key].replace(PepCustomizationService.REM_STRING, '')
            );
            return tmp;
        } else {
            return 0;
        }
    }

    private setFooterHeight(height): void {
        if (this.footerHeight.getValue() !== height) {
            // it is publishing this value to all the subscribers that have already subscribed to this message
            setTimeout(() => {
                this.footerHeight.next(height);
            }, 0);
        }
        document.documentElement.style.setProperty(
            PepCustomizationService.FOOTER_HEIGHT_KEY,
            height + PepCustomizationService.REM_STRING
        );
    }

    hideFooter(): void {
        this.setFooterHeight(0);
    }

    showFooter(): void {
        const themeVars = this.getThemeVariables();

        const res =
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.FOOTER_BAR_SPACING_TOP_KEY
            ) +
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.FOOTER_BAR_SPACING_BOTTOM_KEY
            ) +
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.TOP_BAR_FIELD_HEIGHT_KEY
            );

        this.setFooterHeight(res);
    }

    private setSettingsWidth(width): void {
        if (this.settingsWidth.getValue() !== width) {
            // it is publishing this value to all the subscribers that have already subscribed to this message
            setTimeout(() => {
                this.settingsWidth.next(width);
            }, 0);
        }
        document.documentElement.style.setProperty(
            PepCustomizationService.SETTINGS_WIDTH_KEY,
            width + PepCustomizationService.REM_STRING
        );
    }

    hideSettings(): void {
        this.setSettingsWidth(0);
    }

    showSettings(): void {
        const themeVars = this.getThemeVariables();

        const res = this.getNumberThemeVariable(
            themeVars,
            PepCustomizationService.SIDE_BAR_WIDTH_KEY
        );

        this.setSettingsWidth(res);
    }

    getTopBarHeight(): number {
        const themeVars = this.getThemeVariables();

        const res =
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.TOP_BAR_SPACING_TOP_KEY
            ) +
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.TOP_BAR_SPACING_BOTTOM_KEY
            ) +
            this.getNumberThemeVariable(
                themeVars,
                PepCustomizationService.TOP_BAR_FIELD_HEIGHT_KEY
            );

        return res;
    }

    setOldUserTheme(res: any): void {
        // Added user theme.
        const themeObj = {
            Theme: res.Theme || 'default-theme',
        };

        this.sessionService.setObject(
            PepCustomizationService.USER_THEME,
            JSON.stringify(themeObj)
        );

        let spinnerColor;
        let brandingFontColor;

        if (this.isLightColor(res.BrandingMainColor)) {
            spinnerColor = PepCustomizationService.DEFAULT_SPINNER_COLOR;
            brandingFontColor = '#222';
        } else {
            spinnerColor = res.BrandingMainColor;
            brandingFontColor = PepCustomizationService.DEFAULT_BRANDING_COLOR;
        }

        const color = {
            BrandingSecondaryColor: res.BrandingSecondaryColor,
            BrandingMainColor: res.BrandingMainColor,
            BrandingFontColor: brandingFontColor,
            SpinnerColor: spinnerColor,
        };

        this.sessionService.setObject(
            PepCustomizationService.USER_COLOR,
            JSON.stringify(color)
        );

        this.hasCustomHeader =
            res.TopHeaderFiles &&
            res.TopHeaderFiles.length > 0 &&
            res.TopHeaderFiles[0] !== ''
                ? true
                : false;
        this.hasCustomHomepage =
            res.UserRole === 'Buyer' &&
            res.TopHeaderFiles &&
            res.TopHeaderFiles.length > 1 &&
            res.TopHeaderFiles[1] !== '';
    }

    isLegacyColor(): boolean {
        const h = document.documentElement.style.getPropertyValue(
            PepCustomizationService.COLOR_TOP_HEADER_KEY + '-h'
        );
        const s = document.documentElement.style.getPropertyValue(
            PepCustomizationService.COLOR_TOP_HEADER_KEY + '-s'
        );
        const l = document.documentElement.style.getPropertyValue(
            PepCustomizationService.COLOR_TOP_HEADER_KEY + '-l'
        );

        return !(h && s && l);
    }

    getLoadingSpinnerColor(): string {
        let spinnerColor = '';

        if (this.isLegacyColor()) {
            const color = this.sessionService.getObject(
                PepCustomizationService.USER_COLOR
            );

            if (color && color.SpinnerColor) {
                spinnerColor = color.SpinnerColor;
            } else {
                spinnerColor = PepCustomizationService.DEFAULT_SPINNER_COLOR;
            }
        }

        return spinnerColor;
    }

    getBrandingMainColor(): string {
        let brandingMainColor = '';

        if (this.isLegacyColor()) {
            const color = this.sessionService.getObject(
                PepCustomizationService.USER_COLOR
            );

            if (color && color.BrandingMainColor) {
                brandingMainColor = color.BrandingMainColor;
            } else {
                brandingMainColor =
                    PepCustomizationService.DEFAULT_BRANDING_COLOR;
            }
        }

        return brandingMainColor;
    }

    isLightColor(hexColor): boolean {
        if (!hexColor) {
            return;
        }

        let isBright = false;
        let sum = 0;
        const c = hexColor.replace(/^#/, '');

        sum = parseInt(c[0] + c[1], 16);
        sum += parseInt(c[2] + c[3], 16);
        sum += parseInt(c[4] + c[5], 16);

        if (sum > 382.6) {
            // it's bright color
            isBright = true;
        }

        return isBright;
    }
}

import { Injectable } from '@angular/core';
import {
    FieldLayout,
    ObjectsDataRow,
    UIControl,
    UIControlField,
    ObjectsDataRowCell,
    FIELD_TYPE,
    X_ALIGNMENT_TYPE,
} from '../model/wapi.model';
import { PepGuid } from '../model/utilities.model';

export class PepFieldData {
    ApiName: string;
    Enabled?: boolean;
    Value: string;
    FormattedValue?: string;
    FieldType: FIELD_TYPE;
    ReadOnly?: boolean;
    ColumnWidth: number;
    ColumnWidthType?: number;
    XAlignment: X_ALIGNMENT_TYPE;
    Title: string;
    AdditionalValue?: string;
    OptionalValues?: any;

    constructor(
        options: {
            ApiName?: string;
            Enabled?: boolean;
            Value?: string;
            FormattedValue?: string;
            FieldType?: FIELD_TYPE;
            ReadOnly?: boolean;
            ColumnWidth?: number;
            ColumnWidthType?: number;
            XAlignment?: X_ALIGNMENT_TYPE;
            Title?: string;
        } = {}
    ) {
        this.ApiName = options.ApiName;
        this.Enabled = !!options.Enabled;
        this.Value = options.Value;
        this.FormattedValue = options.FormattedValue || '';
        this.FieldType = options.FieldType;
        this.ReadOnly = !!options.ReadOnly;
        this.ColumnWidth = options.ColumnWidth;
        this.ColumnWidthType = options.ColumnWidthType;
        this.XAlignment = options.XAlignment || X_ALIGNMENT_TYPE.Left;
        this.Title = options.Title || '';
    }
}
export class PepRowData {
    Fields: PepFieldData[];
}

@Injectable({
    providedIn: 'root',
})
export class PepDataConvertorService {
    getUiControl(formData: PepRowData): UIControl {
        const uiControl = new UIControl();

        if (formData?.Fields?.length > 0) {
            const uiRow = formData.Fields;
            uiControl.ControlFields = [];
            uiRow.forEach((field) =>
                uiControl.ControlFields.push(this.setUIControlField(field))
            );
        }

        return uiControl;
    }

    convertFormData(formData: PepRowData): ObjectsDataRow {
        const rowData = new ObjectsDataRow();
        rowData.Fields = [];
        rowData.Type = 0;
        rowData.UID = PepGuid.newGuid();
        formData.Fields.forEach((field) =>
            rowData.Fields.push(this.setDataField(field))
        );

        return rowData;
    }

    convertListData(
        tableData: PepRowData[],
        rowUUID = ''
    ): Array<ObjectsDataRow> {
        const rows = new Array<ObjectsDataRow>();

        if (tableData.length > 0) {
            tableData.forEach((row) => {
                const rowData = new ObjectsDataRow();
                rowData.Fields = [];
                rowData.Type = 0;
                rowData.UID = rowUUID ? rowUUID : PepGuid.newGuid();
                row.Fields.forEach((field) =>
                    rowData.Fields.push(this.setDataField(field))
                );
                rows.push(rowData);
            });
        }

        return rows;
    }

    setUIControlField(field: PepFieldData): UIControlField {
        const controlField = new UIControlField();
        controlField.ApiName = field.ApiName;
        controlField.FieldType = field.FieldType;

        controlField.Layout = new FieldLayout({
            X: 1,
            Width: 1,
            XAlignment: field.XAlignment,
            Y: 1,
            Height: 1,
            YAlignment: 1,
        });

        controlField.Title = field.Title;
        controlField.ReadOnly = field.ReadOnly === true ? true : false;
        controlField.ColumnWidth = field.ColumnWidth;
        controlField.ColumnWidthType = field.ColumnWidthType || 1;
        return controlField;
    }

    setDataField(field: PepFieldData): ObjectsDataRowCell {
        const dataField = new ObjectsDataRowCell();
        dataField.AdditionalValue = field.AdditionalValue;
        dataField.ApiName = field.ApiName;
        dataField.Enabled = field.Enabled === false ? false : true;
        dataField.FieldType = field.FieldType;
        dataField.FormattedValue =
            field.FormattedValue || field.Value.toString();
        dataField.GroupFields = null;
        dataField.NotificationInfo = '';
        dataField.OptionalValues = field.OptionalValues;
        dataField.ReferenceObjectSubType = '';
        dataField.TextColor = '';
        dataField.Value = field.Value.toString();
        dataField.Visible = true;
        return dataField;
    }

    setFieldLayout(
        x: number,
        width: number,
        xAlignment: number,
        y: number,
        height: number,
        yAlignment: number,
        lineNumber: number
    ): FieldLayout {
        const layout = new FieldLayout({
            X: x,
            Width: width,
            XAlignment: xAlignment,
            Y: y,
            Height: height,
            YAlignment: yAlignment,
            LineNumber: lineNumber,
        });

        return layout;
    }
}

import { Injectable, TemplateRef } from '@angular/core';
import {
    MatDialog,
    MatDialogConfig,
    MatDialogRef,
} from '@angular/material/dialog';
import { PepLayoutService } from '@pepperi-addons/ngx-lib';
import { Overlay } from '@angular/cdk/overlay';
import { ComponentType } from '@angular/cdk/portal';
import { PepDialogSizeType, PepDialogData } from './dialog.model';
import { PepDefaultDialogComponent } from './default-dialog.component';

@Injectable({
    providedIn: 'root',
})
export class PepDialogService {
    constructor(
        private dialog: MatDialog,
        private layoutService: PepLayoutService,
        private overlay: Overlay
    ) {}

    getDialogConfig(
        options: {
            disableClose?: boolean;
            height?: string;
            minWidth?: string;
            maxWidth?: string;
            maxHeight?: string;
            panelClass?: string;
        } = {},
        size: PepDialogSizeType = 'regular'
    ): MatDialogConfig {
        const dialogConfig = new MatDialogConfig();

        dialogConfig.autoFocus = false;
        dialogConfig.direction = this.layoutService.isRtl() ? 'rtl' : 'ltr';
        dialogConfig.disableClose = options.disableClose ?? true;
        dialogConfig.panelClass = ['pep-dialog', options.panelClass || ''];

        if (size === 'inline') {
            dialogConfig.height = options.height || 'auto';
            dialogConfig.maxWidth = options.maxWidth || '100vw';
            dialogConfig.maxHeight = options.maxHeight || '100vh';
            dialogConfig.minWidth = options.minWidth || '300px';
        }

        dialogConfig.panelClass.push(size);

        dialogConfig.scrollStrategy = this.overlay.scrollStrategies.noop();
        // dialogConfig.scrollStrategy = this.overlay.scrollStrategies.block();

        return dialogConfig;
    }

    openDefaultDialog(
        data: PepDialogData,
        config: MatDialogConfig = null
    ): MatDialogRef<any> {
        if (!config) {
            config = this.getDialogConfig();
        }

        config.data = data;
        const dialogRef = this.dialog.open(PepDefaultDialogComponent, config);
        return dialogRef;
    }

    openDialog<T>(
        componentOrTemplateRef: ComponentType<T> | TemplateRef<T>,
        data: any = {},
        config: MatDialogConfig = null
    ): MatDialogRef<T> {
        if (!config) {
            config = this.getDialogConfig();
        }

        config.data = data;

        const dialogRef = this.dialog.open(componentOrTemplateRef, config);
        return dialogRef;
    }
}

import { Injectable } from '@angular/core';

export type PepFileType = 'script' | 'style';

export interface IPepExternalFileModel {
    path: string;
    type: PepFileType;
}

declare let document: any;

@Injectable({
    providedIn: 'root',
})
export class PepFileService {
    private scripts: Map<string, { loaded: boolean; src: string }>;
    private styles: Map<string, { loaded: boolean; src: string }>;

    constructor() {
        this.scripts = new Map<string, { loaded: boolean; src: string }>();
        this.styles = new Map<string, { loaded: boolean; src: string }>();
    }

    loadFiles(files: IPepExternalFileModel[]): Promise<any[]> {
        const promises: any[] = [];
        files.forEach((file) => {
            if (file.type === 'style') {
                promises.push(this.loadStyle(file.path));
            } else if (file.type === 'script') {
                promises.push(this.loadScript(file.path));
            }
        });
        return Promise.all(promises);
    }

    removeFiles(files: IPepExternalFileModel[]): void {
        for (
            let index = 0;
            index < files.length &&
            files[index].path &&
            files[index].path.trim() !== '';
            index++
        ) {
            const name = this.getFileName(files[index].path, true);
            const element = document.getElementById(name);
            element.parentNode.removeChild(element);

            if (files[index].type === 'script' && this.scripts.has(name)) {
                this.scripts.delete(name);
            } else if (files[index].type === 'style' && this.styles.has(name)) {
                this.styles.delete(name);
            }
        }
    }

    loadScript(path: string): Promise<any> {
        return new Promise((resolve, reject) => {
            const name = this.getFileName(path, true);

            // If the script isn't exist add it.
            if (!this.scripts.has(name)) {
                this.scripts.set(name, { loaded: false, src: path });
            }

            const scriptItem = this.scripts.get(name);

            // Resolve if already loaded
            if (scriptItem.loaded) {
                resolve({
                    script: name,
                    loaded: true,
                    status: 'Already Loaded',
                });
            } else {
                // Load script
                const script = document.createElement('script');
                script.type = 'text/javascript';
                script.src = scriptItem.src;
                script.setAttribute('id', name);
                script.async = false;

                if (script.readyState) {
                    // IE
                    script.onreadystatechange = () => {
                        if (
                            script.readyState === 'loaded' ||
                            script.readyState === 'complete'
                        ) {
                            script.onreadystatechange = null;
                            scriptItem.loaded = true;
                            resolve({
                                path,
                                type: 'script',
                                loaded: true,
                                status: 'Loaded',
                            });
                        }
                    };
                } else {
                    // Others
                    script.onload = () => {
                        scriptItem.loaded = true;
                        resolve({
                            path,
                            type: 'script',
                            loaded: true,
                            status: 'Loaded',
                        });
                    };
                }
                script.onerror = (error: any) =>
                    resolve({
                        path,
                        type: 'script',
                        loaded: false,
                        status: 'Loaded',
                    });
                document.getElementsByTagName('head')[0].appendChild(script);
            }
        });
    }

    loadStyle(path: string): Promise<any> {
        return new Promise((resolve, reject) => {
            const name = this.getFileName(path, true);

            // If the style isn't exist add it.
            if (!this.styles.has(name)) {
                this.styles.set(name, { loaded: false, src: path });
            }

            const styleItem = this.styles.get(name);

            // Resolve if already loaded
            if (styleItem.loaded) {
                resolve({
                    path,
                    type: 'style',
                    loaded: true,
                    status: 'Already Loaded',
                });
            } else {
                // Load style
                const style = document.createElement('link');
                style.type = 'text/css';
                style.rel = 'stylesheet';
                style.href = styleItem.src;
                style.media = 'all';
                style.setAttribute('id', name);

                styleItem.loaded = true;
                resolve({
                    path,
                    type: 'style',
                    loaded: true,
                    status: 'Loaded',
                });

                document.getElementsByTagName('head')[0].appendChild(style);
            }
        });
    }

    loadFontStyle(styleId: string, href: string): void {
        const head = document.getElementsByTagName('head')[0];

        const styleElement = document.getElementById(
            styleId
        ) as HTMLLinkElement;

        if (styleElement) {
            styleElement.href = href;
        } else {
            const style = document.createElement('link');
            style.id = styleId;
            style.rel = 'stylesheet';
            style.href = `${href}`;

            head.appendChild(style);
        }
    }

    getFileName(filePath: string, withExtenstion = false): string {
        const lastIndex = withExtenstion
            ? filePath.length - 1
            : filePath.lastIndexOf('.');

        return filePath.substr(filePath.lastIndexOf('/') + 1, lastIndex);
    }

    getFileExtension(filePath: string): string {
        const fileSplit = filePath.split('.');
        let fileExt = '';
        if (fileSplit.length > 1) {
            fileExt = fileSplit[fileSplit.length - 2];
        }
        return fileExt;
    }

    /* Returns true if url is valid */
    isValidUrl(url: string): boolean {
        /* Try creating a valid URL */
        try {
            const tmp = new URL(url);
            return true;
        } catch (e) {
            return false;
        }
    }

    convertFromb64toBlob(
        b64Data: any,
        contentType = '',
        sliceSize = 512
    ): Blob {
        const byteCharacters = atob(b64Data);
        const byteArrays = [];

        for (
            let offset = 0;
            offset < byteCharacters.length;
            offset += sliceSize
        ) {
            const slice = byteCharacters.slice(offset, offset + sliceSize);

            const byteNumbers = new Array(slice.length);
            for (let i = 0; i < slice.length; i++) {
                byteNumbers[i] = slice.charCodeAt(i);
            }

            const byteArray = new Uint8Array(byteNumbers);
            byteArrays.push(byteArray);
        }

        const blob = new Blob(byteArrays, { type: contentType });
        return blob;
    }

    getAssetsPath(assetsDomain = ''): string {
        const concatChar =
            assetsDomain === '' || assetsDomain.endsWith('/') ? '' : '/';
        return `${assetsDomain}${concatChar}assets/ngx-lib/`;
    }

    getAssetsTranslationsSuffix(): string {
        return '.ngx-lib.json';
    }

    getAssetsTranslationsPath(assetsDomain = ''): string {
        return `${this.getAssetsPath(assetsDomain)}i18n/`;
    }

    getAssetsImagesPath(assetsDomain = '', image = ''): string {
        return `${this.getAssetsPath(assetsDomain)}images/${image}`;
    }

    getSvgAsImageSrc(svg: string): string {
        const blob = new Blob([svg], { type: 'image/svg+xml' });
        const url = URL.createObjectURL(blob);
        return url;
    }
}

import { Observable } from 'rxjs';
import {
    Component,
    OnInit,
    Injectable,
    Input,
    Output,
    EventEmitter,
    ViewChild,
    ChangeDetectorRef,
    ChangeDetectionStrategy,
    Optional,
} from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { FileUploader } from 'ng2-file-upload';
import { FormGroup } from '@angular/forms';
import {
    PepFileService,
    PepLayoutType,
    PepCustomizationService,
    PepHorizontalAlignment,
    DEFAULT_HORIZONTAL_ALIGNMENT,
    IPepFieldClickEvent,
} from '@pepperi-addons/ngx-lib';
import {
    PepDialogService,
    PepDialogData,
} from '@pepperi-addons/ngx-lib/dialog';
import { pepIconNoImage2 } from '@pepperi-addons/ngx-lib/icon';

@Component({
    selector: 'pep-files-uploader',
    templateUrl: './files-uploader.component.html',
    styleUrls: ['./files-uploader.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
@Injectable()
export class PepFilesUploaderComponent implements OnInit {
    @Input() key = '';
    @Input() src = '';
    @Input() label = '';
    @Input() required = false;
    @Input() disabled = false;
    @Input() xAlignment: PepHorizontalAlignment = DEFAULT_HORIZONTAL_ALIGNMENT;
    @Input() rowSpan = 1;
    @Input() controlType = '';
    @Input() sizeLimitMB = 5;

    @Input() form: FormGroup;
    @Input() standAlone = false;
    @Input() acceptedExtensions = 'bmp,jpg,jpeg,png,gif,ico,svg,html,css';
    @Input() layoutType: PepLayoutType = 'form';

    @Output() fileChange: EventEmitter<string> = new EventEmitter<string>();
    @Output()
    elementClick: EventEmitter<IPepFieldClickEvent> = new EventEmitter<IPepFieldClickEvent>();

    @ViewChild('fileInput') fileInput: any;
    @ViewChild('imagePreview') imagePreview: any;

    fieldHeight = '';

    // multiple = false;
    uploader: FileUploader;
    progress = 0;
    // response: string;
    intervalID: any = null;

    constructor(
        private dialogService: PepDialogService,
        private customizationService: PepCustomizationService,
        private fileService: PepFileService,
        private translate: TranslateService
    ) {
        this.uploader = new FileUploader({ removeAfterUpload: true });

        this.uploader.onAfterAddingFile = (item) => {
            if (this.fileInput?.nativeElement) {
                this.fileInput.nativeElement.value = '';
            }
            const reader = new FileReader();

            reader.onload = (event: any) => {
                const fileExt = item._file.name.split('.').pop();
                const target = event.target || event.srcElement;
                const fileStr = target.result;
                const errorMsg = this.isValidFile(
                    fileStr,
                    fileExt,
                    this.acceptedExtensions,
                    this.sizeLimitMB
                );
                if (errorMsg === '') {
                    this.src = fileStr;
                    this.setIntervalX(25, 75);
                    this.setProgress(5);
                    this.fileChange.emit(
                        JSON.stringify({
                            acceptedExtensions: this.acceptedExtensions,
                            fileStr,
                            fileExt,
                        })
                    );
                } else {
                    const title = this.translate.instant(
                        'MESSAGES.TITLE_NOTICE'
                    );
                    const data = new PepDialogData({
                        title,
                        content: errorMsg,
                    });
                    this.dialogService.openDefaultDialog(data);
                }
            };
            reader.readAsDataURL(item._file);
        };
    }

    ngOnInit(): void {
        this.fieldHeight = this.customizationService.calculateFieldHeight(
            this.layoutType,
            this.rowSpan,
            this.standAlone
        );

        /*this.uploader.onCompleteAll = () => {
            this.fileInput.nativeElement.value = '';
        }*/
    }

    isValidFile(
        fileStr,
        fileExtension,
        acceptedExtensions,
        sizeLimitMB = 5
    ): string {
        const file: any = fileStr;
        let fileSize = 0;
        let content = '';
        // check if got file as Base64
        if (typeof fileStr === 'string' && fileStr.indexOf('data:') > -1) {
            fileSize = this.getBase64FileSize(fileStr);
        } else {
            fileSize = file.size;
        }
        // check the size and the extension
        const sizeOK: boolean =
            fileSize !== -1 && file != null && fileSize < sizeLimitMB * 1048576;
        const extensionOK =
            acceptedExtensions === '' ||
            acceptedExtensions.indexOf(fileExtension.toLowerCase()) !== -1;
        if (!extensionOK) {
            content = this.translate.instant(
                'MESSAGES.ERROR_FAILD_TO_LOAD_EXTENSION',
                {
                    fileExtension:
                        "<label class='uppercase bold'>" +
                        fileExtension +
                        '</label>',
                }
            );
        } else if (!sizeOK) {
            content = this.translate.instant(
                'MESSAGES.ERROR_FAILD_TO_LOAD_SIZE',
                {
                    fileSize:
                        "<label class='uppercase bold'>" +
                        sizeLimitMB.toString() +
                        '</label>',
                }
            );
        }
        return content;
    }

    getBase64FileSize(base64String: string): number {
        let fileSize: number;

        try {
            base64String = base64String.substr(base64String.indexOf(',') + 1);
            fileSize = atob(base64String).length;
        } catch (e) {
            fileSize = -1;
        }

        return fileSize; // return size in bytes;
    }

    setIntervalX(delay, repetitions): void {
        let x = 0;
        this.intervalID = window.setInterval(() => {
            // this.setProgress(this.progress + 5);
            if (++x === repetitions || this.uploader.progress >= 100) {
                window.clearInterval(this.intervalID);
            }
        }, delay);
    }

    errorHandler(event): void {
        event.target.src = this.fileService.getSvgAsImageSrc(
            pepIconNoImage2.data
        );
        event.target.title = this.translate.instant('IMAGE.NO_IMAGE');
    }

    private setProgress(progress): void {
        this.progress = progress;
        this.uploader.progress = progress;
    }

    deleteFile(): void {
        this.uploader.clearQueue();
        window.clearInterval(this.intervalID);
        this.setProgress(0);

        const value = '';
        this.src = value;
        this.fileChange.emit(value);
    }

    onElementClicked(event): void {
        this.elementClick.emit({
            key: this.key,
            controlType: this.controlType,
            eventWhich: event.which,
        });
    }

    onClick_ChooseFile(event): void {
        if (this.fileInput?.nativeElement) {
            this.fileInput.nativeElement.click();
        }
    }

    onKeyPress_ChooseFile(event): void {
        const e = event as KeyboardEvent;

        if ([13, 32].indexOf(e.which) !== -1) {
            if (this.fileInput?.nativeElement) {
                this.fileInput.nativeElement.click();
            }
        }
        e.preventDefault();
    }
}

import { Injectable } from '@angular/core';
import {
    HttpClient,
    HttpErrorResponse,
    HttpParams,
    HttpHeaders,
} from '@angular/common/http';
import { throwError, Observable } from 'rxjs';
import { retry, catchError, tap } from 'rxjs/operators';
import { PepSessionService } from '../../common/services/session.service';
import { PepCookieService } from '../../common/services/cookie.service';

@Injectable({
    providedIn: 'root',
})
export class PepHttpService {
    private readonly AUTH_HEADER = 'Authorization';
    private readonly PEPPERI_TOKEN_HEADER = 'PepperiSessionToken';
    private readonly WAPI_TOKEN_KEY = 'auth_token';
    private readonly PEPPERI_TOKEN_COOKIE = 'PepperiUserSettings';

    constructor(
        private sessionService: PepSessionService,
        private cookieService: PepCookieService,
        private http: HttpClient
    ) {}

    private handleError(response: HttpErrorResponse): Observable<never> {
        let errorMessage = 'Unknown error!';
        if (response.error instanceof ErrorEvent) {
            // Client-side errors
            errorMessage = `Error: ${response.error.message}`;
        } else {
            // Server-side errors
            errorMessage = this.getServerErrorMessage(response);
        }

        return throwError(errorMessage);
    }

    private getServerErrorMessage(response: HttpErrorResponse): string {
        if (response?.error?.fault?.faultstring) {
            return `${response.error.fault.faultstring}`;
        } else {
            switch (response.status) {
                case 404: {
                    return `Not Found: ${response.message}`;
                }
                case 403: {
                    return `Access Denied: ${response.message}`;
                }
                case 500: {
                    return `Internal Server Error: ${response.message}`;
                }
                default: {
                    return `Unknown Server Error\nError Code: ${response.status}\nMessage: ${response.message}`;
                }
            }
        }
    }

    // Add authorization token if the token exist.
    private addAuthorizationToken(httpOptions: any = {}): any {
        if (!httpOptions.headers.has(this.AUTH_HEADER)) {
            const idpToken = this.sessionService.getIdpToken();

            if (idpToken) {
                httpOptions.headers = httpOptions.headers.set(
                    this.AUTH_HEADER,
                    `Bearer ${idpToken}`
                );
            }
        }

        return httpOptions;
    }

    // Add web api token if calling to wapi domain.
    private addWebApiToken(url: string, httpOptions: any = {}): any {
        if (!httpOptions.headers.has(this.PEPPERI_TOKEN_HEADER)) {
            const wapiBaseUrl = this.sessionService.getWapiBaseUrl();

            if (wapiBaseUrl && url.match(new RegExp(wapiBaseUrl, 'g'))) {
                // TODO:
                // const webApiToken = this.sessionService.getWapiToken();
                try {
                    const userSettingCookie = this.cookieService.get(
                        this.PEPPERI_TOKEN_COOKIE
                    );
                    const webApiToken = JSON.parse(userSettingCookie).values
                        .items[this.WAPI_TOKEN_KEY];
                    if (webApiToken) {
                        httpOptions.headers = httpOptions.headers.set(
                            this.PEPPERI_TOKEN_HEADER,
                            webApiToken
                        );
                    }
                } catch {
                    // Do nothing.
                }
            }
        }

        return httpOptions;
    }

    private setDefaultHeaderOptions(url: string, httpOptions: any = {}): any {
        if (!httpOptions.headers) {
            httpOptions.headers = new HttpHeaders();
        }

        // Add content type
        if (!httpOptions.headers.has('Content-Type')) {
            httpOptions.headers = httpOptions.headers.set(
                'Content-Type',
                'application/json'
            );
        }

        httpOptions = this.addAuthorizationToken(httpOptions);
        httpOptions = this.addWebApiToken(url, httpOptions);

        return httpOptions;
    }

    // getTextFile(filename: string): void {
    //     // The Observable returned by get() is of type Observable<string>
    //     // because a text response was specified.
    //     // There's no need to pass a <string> type parameter to get().
    //     // return this.http.get(filename, {responseType: 'text'})
    //     //     .pipe(
    //     //         tap( // Log the result or error
    //     //         data => this.log(filename, data),
    //     //         error => this.logError(filename, error)
    //     //         )
    //     // );
    // }

    getHttpCall(url: string, httpOptions: any = {}): Observable<any> {
        httpOptions = this.setDefaultHeaderOptions(url, httpOptions);

        return this.http
            .get(url, httpOptions)
            .pipe(catchError(this.handleError.bind(this)));
        //     .subscribe(
        //         (res) => console.log(''),
        //         (error) => console.log(''),
        //         () => {}
        // );
    }

    postHttpCall(
        url: string,
        body: any | null,
        httpOptions: any = {}
    ): Observable<any> {
        httpOptions = this.setDefaultHeaderOptions(url, httpOptions);

        return this.http
            .post(url, body, httpOptions)
            .pipe(catchError(this.handleError.bind(this)));
        //     .subscribe(
        //         (res) => console.log(''),
        //         (error) => console.log(''),
        //         () => {}
        // );
    }

    getWapiApiCall(url: string, httpOptions: any = {}): Observable<any> {
        const wapiBaseUrl = this.sessionService.getWapiBaseUrl();
        return this.getHttpCall(`${wapiBaseUrl}${url}`, httpOptions);
    }

    postWapiApiCall(
        url: string,
        body: any | null,
        httpOptions: any = {}
    ): Observable<any> {
        const wapiBaseUrl = this.sessionService.getWapiBaseUrl();
        return this.postHttpCall(`${wapiBaseUrl}${url}`, body, httpOptions);
    }

    getPapiApiCall(url: string, httpOptions: any = {}): Observable<any> {
        const papiBaseUrl = this.sessionService.getPapiBaseUrl();
        return this.getHttpCall(`${papiBaseUrl}${url}`, httpOptions);
    }

    postPapiApiCall(
        url: string,
        body: any | null,
        httpOptions: any = {}
    ): Observable<any> {
        const papiBaseUrl = this.sessionService.getPapiBaseUrl();
        return this.postHttpCall(`${papiBaseUrl}${url}`, body, httpOptions);
    }
}

import { Injectable } from '@angular/core';
import { IPepIconData, PepIconType } from './icon-generated.model';

@Injectable({
    providedIn: 'root',
})
export class PepIconRegistry {
    private registry = new Map<PepIconType, string>();

    public registerIcons(icons: IPepIconData[]): void {
        icons.forEach((icon: IPepIconData) =>
            this.registry.set(icon.name, icon.data)
        );
    }

    public getIcon(iconName: PepIconType): string | undefined {
        if (!this.registry.has(iconName)) {
            console.warn(`We could not find the Icon with the name ${iconName},
                did you add it to the Icon registry?`);
        }
        return this.registry.get(iconName);
    }
}

