import {
    Component,
    ViewChild,
    OnInit,
    OnDestroy,
    AfterViewInit,
    ElementRef,
    Input,
    TemplateRef,
    Renderer2,
    ChangeDetectorRef,
    Inject,
    Optional,
    ViewContainerRef,
} from '@angular/core';
import {
    trigger,
    state,
    style,
    transition,
    animate,
} from '@angular/animations';
import { FormGroup } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { TranslateService } from '@ngx-translate/core';
import {
    Gallery,
    GalleryConfig,
    LoadingStrategy,
    SlidingDirection,
    ThumbnailsPosition,
    GalleryItem,
    ImageItem,
    GalleryRef,
} from 'ng-gallery';
import {
    PepCustomizationService,
    PepLayoutType,
    PepLayoutService,
    PepFileService,
    PepHorizontalAlignment,
    DEFAULT_HORIZONTAL_ALIGNMENT,
    PepImagesField,
} from '@pepperi-addons/ngx-lib';
import { PepDialogService } from '@pepperi-addons/ngx-lib/dialog';
import { pepIconArrowRightAlt } from '@pepperi-addons/ngx-lib/icon';
import { pepIconNoImage2 } from '@pepperi-addons/ngx-lib/icon';

import 'hammerjs';

interface IPepImagesFilmstripDialogData {
    currIndex: number;
    key: string;
    value: string;
    label: string;
    uid: string;
    showThumbnails: boolean;
}

// export const slideInAnimation = trigger('slideAnimation', [
//     state('in', style({ transform: 'translateY(0)', opacity: 1 })),
//     transition(':enter', [
//         style({ transform: 'translateY(-100%)', opacity: 0 }),
//         animate(400)
//     ]),
//     transition(':leave', [
//         animate(400, style({ transform: 'translateY(-100%)', opacity: 0 }))
//     ])
// ]);

import {
    IMAGEVIEWER_CONFIG,
    ImageViewerConfig,
    createButtonConfig,
} from '@hallysonh/ngx-imageviewer';

export function createViewerConfig(
    translate: TranslateService
): ImageViewerConfig {
    return {
        // width: 850,
        // height: 150,
        bgStyle: '#FFF', // component background style
        // scaleStep: 0.1, // zoom scale step (using the zoom in/out buttons)
        // rotateStepper: false, // touch rotate should rotate only 90 to 90 degrees
        loadingMessage: translate.instant('MESSAGES.INFO_LOADING_FILE'), // 'Loading...',
        // buttonStyle: {
        //     iconFontFamily: 'Material Icons', // font used to render the button icons
        //     alpha: 0.5, // buttons' transparence value
        //     hoverAlpha: 0.7, // buttons' transparence value when mouse is over
        //     bgStyle: '#000000', //  buttons' background style
